CREATE OR REPLACE FUNCTION pls_obter_contratos_mens (nr_seq_mensalidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000)	:= null;
nr_contrato_w		varchar(20);

C01 CURSOR FOR
	SELECT	distinct(d.nr_contrato)
	from	pls_contrato d,
		pls_segurado c,
		pls_mensalidade_segurado b,
		pls_mensalidade a
	where	a.nr_sequencia	= b.nr_seq_mensalidade
	and	c.nr_sequencia	= b.nr_seq_segurado
	and	d.nr_sequencia	= c.nr_seq_contrato
	and	a.nr_sequencia	= nr_seq_mensalidade_p;


BEGIN

ds_retorno_w	:= '';

open C01;
loop
fetch C01 into
	nr_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= nr_contrato_w;
	else
		ds_retorno_w	:= ds_retorno_w || ', ' || nr_contrato_w;
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
