CREATE OR REPLACE FUNCTION pls_obter_contratos_mes ( dt_mes_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_retorno_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_retorno_w
from	pls_contrato
where 	trunc(dt_aprovacao,'month') <= trunc(dt_mes_referencia_p,'month')
and 	((coalesce(dt_rescisao_contrato::text, '') = '') or (trunc(dt_rescisao_contrato,'month') > trunc(dt_mes_referencia_p,'month')));

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
