CREATE OR REPLACE FUNCTION pls_obter_contrato_benef_pag ( nr_seq_pagador_p bigint, nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


nr_contrato_w		bigint;


BEGIN
if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') then
	select	b.nr_contrato
	into STRICT	nr_contrato_w
	from	pls_contrato 		b,
		pls_contrato_pagador	a
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_pagador_p;
elsif (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	b.nr_contrato
	into STRICT	nr_contrato_w
	from	pls_segurado		a,
		pls_contrato		b
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_segurado_p;
end if;

return	nr_contrato_w;

end;
$body$
