CREATE OR REPLACE FUNCTION pls_obter_contrato_ops_inter ( nr_seq_contrato_p bigint, nr_seq_intercambio_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);


BEGIN 
 
if (coalesce(nr_seq_contrato_p,0) <> 0) then 
	begin 
	select	substr(obter_nome_pf_pj(cd_pf_estipulante, cd_cgc_estipulante),1,255) 
	into STRICT	ds_retorno_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
	exception 
	when others then 
		ds_retorno_w	:= '';
	end;
elsif (coalesce(nr_seq_intercambio_p,0) <> 0) then 
	begin 
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc),1,200) 
	into STRICT	ds_retorno_w 
	from	pls_intercambio 
	where	nr_sequencia	= nr_seq_intercambio_p;
	exception 
	when others then 
		ds_retorno_w	:= '';
	end;
else 
	ds_retorno_w	:= '';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
