CREATE OR REPLACE FUNCTION pls_obter_contrato_tabela (nr_seq_tabela_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_contrato_w		bigint;
ds_contrato_w			varchar(2000)	:= '';

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_contrato a,
		pls_segurado b
	where	nr_seq_tabela_p <> 0
	and	b.nr_seq_tabela	= nr_seq_tabela_p
	and	a.nr_sequencia	= b.nr_seq_contrato
	and	coalesce(b.dt_rescisao::text, '') = ''
	
union

	SELECT	a.nr_sequencia
	from	pls_contrato a,
		pls_contrato_plano b
	where	nr_seq_tabela_p <> 0
	and	b.nr_seq_tabela	= nr_seq_tabela_p
	and	a.nr_sequencia	= b.nr_seq_contrato
	group by a.nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (length(coalesce(ds_contrato_w,'0')) < 1980) then
		ds_contrato_w	:= ds_contrato_w || nr_seq_contrato_w || ', ';
	end if;
end loop;
close c01;

if (length(ds_contrato_w) > 2) then
	ds_contrato_w	:= substr(ds_contrato_w, 1, length(ds_contrato_w) - 2);
end if;

return ds_contrato_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_contrato_tabela (nr_seq_tabela_p bigint) FROM PUBLIC;

