CREATE OR REPLACE FUNCTION pls_obter_contrato_titulo ( nr_titulo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

select	max(d.nr_sequencia)
into STRICT	ds_retorno_w
from	pls_contrato d,
	pls_contrato_pagador c,
	pls_mensalidade b,
	titulo_receber a
where	a.nr_seq_mensalidade	= b.nr_sequencia
and	b.nr_seq_pagador  	= c.nr_sequencia
and	c.nr_seq_contrato	= d.nr_sequencia
and	a.nr_titulo		= nr_titulo_p;

return	ds_retorno_w;

end;
