SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_conv_serv_extratmens ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, nr_seq_material_p pls_material.nr_sequencia%type, dt_referencia_p timestamp, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_prest_exec_p pls_prestador.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_servico_w			varchar(255);
cd_area_w			bigint;
cd_especialidade_w		bigint;
cd_grupo_w			bigint;
ie_origem_proced_ww		bigint;
ie_tipo_despesa_w		pls_conta_proc.ie_tipo_despesa%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
qt_coop_w			bigint;
ie_cooperado_w			varchar(1);
ie_prest_exec_w			varchar(1);
cd_pf_prest_w			pls_prestador.cd_pessoa_fisica%type;
cd_cgc_prest_w			pls_prestador.cd_cgc%type;
qt_coop_prest_w			integer;

C01 CURSOR FOR
	SELECT	a.ds_servico_dest
	from	pls_conv_serv_extrat_mens	a
	where	coalesce(a.nr_seq_material, 1) 	= 1
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p)
	and (coalesce(a.ie_origem_proced,ie_origem_proced_ww) = ie_origem_proced_ww))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento	= cd_area_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade		= cd_especialidade_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc		= cd_grupo_w))
	and	((coalesce(a.ie_tipo_despesa::text, '') = '') or (a.ie_tipo_despesa		= ie_tipo_despesa_w))
	and	coalesce(a.ie_atend_internacao,'N') = 'N'
	and	((coalesce(ie_cooperado_w,'A') = 'A') or (coalesce(a.ie_benef_cooperado,'A') = ie_cooperado_w) or (coalesce(a.ie_benef_cooperado,'A') = 'A'))
	and	((coalesce(ie_prest_exec_w,'A') = 'A') or (coalesce(a.ie_prestador_exec_cooperado,'A') = ie_prest_exec_w) or (coalesce(a.ie_prestador_exec_cooperado,'A') = 'A'));

C02 CURSOR FOR
	SELECT	a.ds_servico_dest
	from	pls_conv_serv_extrat_mens	a
	where	a.nr_seq_material 		= nr_seq_material_p
	and	coalesce(a.ie_atend_internacao,'N') = 'N'
	and	((coalesce(ie_cooperado_w,'A') = 'A') or (coalesce(a.ie_benef_cooperado,'A') = ie_cooperado_w) or (coalesce(a.ie_benef_cooperado,'A') = 'A'))
	and	((coalesce(ie_prest_exec_w,'A') = 'A') or (coalesce(a.ie_prestador_exec_cooperado,'A') = ie_prest_exec_w) or (coalesce(a.ie_prestador_exec_cooperado,'A') = 'A'));


BEGIN

if	(nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '' AND dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;

	select	count(1)
	into STRICT	qt_coop_w
	from	pls_cooperado
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	ie_status = 'A'
	and	dt_inclusao <= dt_referencia_p;

	if (qt_coop_w > 0) then
		ie_cooperado_w := 'C';
	else
		ie_cooperado_w := 'N';
	end if;
else
	ie_cooperado_w := 'A';
end if;

if	(nr_seq_prest_exec_p IS NOT NULL AND nr_seq_prest_exec_p::text <> '' AND dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	select	max(cd_pessoa_fisica),
		max(cd_cgc)
	into STRICT	cd_pf_prest_w,
		cd_cgc_prest_w
	from	pls_prestador
	where	nr_sequencia = nr_seq_prest_exec_p;

	if (cd_pf_prest_w IS NOT NULL AND cd_pf_prest_w::text <> '') then
		select	count(1)
		into STRICT	qt_coop_prest_w
		from	pls_cooperado
		where	cd_pessoa_fisica = cd_pf_prest_w
		and	ie_status = 'A'
		and	dt_inclusao <= dt_referencia_p;
	elsif (cd_cgc_prest_w IS NOT NULL AND cd_cgc_prest_w::text <> '') then
		select	count(1)
		into STRICT	qt_coop_prest_w
		from	pls_cooperado
		where	cd_cgc = cd_cgc_prest_w
		and	ie_status = 'A'
		and	dt_inclusao <= dt_referencia_p;
	end if;

	if (qt_coop_prest_w > 0) then
		ie_prest_exec_w := 'C';
	else
		ie_prest_exec_w := 'N';
	end if;
else
	ie_prest_exec_w := 'A';
end if;

if (coalesce(nr_seq_material_p::text, '') = '') then
	SELECT * FROM pls_obter_estrut_proc( cd_procedimento_p, ie_origem_proced_p, cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proced_ww ) INTO STRICT cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proced_ww;
	ie_origem_proced_ww	:= coalesce(ie_origem_proced_ww, ie_origem_proced_p);

	select	max(ie_tipo_despesa)
	into STRICT	ie_tipo_despesa_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_conta_proc_p;

	open C01;
	loop
	fetch C01 into
		ds_servico_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
else
	open C02;
	loop
	fetch C02 into
		ds_servico_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		null;
		end;
	end loop;
	close C02;
end if;

return ds_servico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_conv_serv_extratmens ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, nr_seq_material_p pls_material.nr_sequencia%type, dt_referencia_p timestamp, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_prest_exec_p pls_prestador.nr_sequencia%type) FROM PUBLIC;

