CREATE OR REPLACE FUNCTION pls_obter_coop_pag_resp_fin ( nr_seq_congenere_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_pagador_w	bigint;
cd_cgc_resp_w		varchar(14);


BEGIN
if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_pagador_w
	from	pls_contrato_pagador a
	where	nr_seq_congenere = nr_seq_congenere_p;

	if (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') then
		select	max(a.cd_cgc_resp)
		into STRICT	cd_cgc_resp_w
		from	pls_contrato_pag_resp_fin a
		where	a.nr_seq_pagador = nr_seq_pagador_w
		and	coalesce(dt_referencia_p,clock_timestamp()) between trunc(a.dt_inicio_vigencia,'dd') and
			fim_dia(coalesce(a.dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp())));
	end if;
end if;

return	cd_cgc_resp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
