SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_coop_resp_financ ( nr_sequencia_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_resp_financ_w	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(a.nr_seq_cong_resp_financ)
	into STRICT	nr_seq_resp_financ_w
	from	pls_congenere_resp_financ a
	where	a.nr_seq_congenere	= nr_sequencia_p
	and	coalesce(dt_referencia_p,clock_timestamp()) between trunc(a.dt_inicio_vigencia,'dd') and
			fim_dia(coalesce(a.dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp())));

	/*  Se não encontrar regra de responsável o responsável é a própria cooperativa */

	if (coalesce(nr_seq_resp_financ_w::text, '') = '') then
		nr_seq_resp_financ_w	:= nr_sequencia_p;
	end if;
end if;

return	nr_seq_resp_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
