SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_copartic_reajuste ( nr_seq_contrato_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_copartic_reajuste_w	varchar(1);


BEGIN

select	CASE WHEN max(nr_seq_plano)='' THEN 'C'  ELSE 'P' END
into STRICT	ie_copartic_reajuste_w
from	pls_tipo_coparticipacao a,
	pls_regra_coparticipacao b
where	b.nr_seq_tipo_coparticipacao = a.nr_sequencia
and	a.ie_situacao = 'A'
and	a.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
and	a.nr_sequencia = nr_sequencia_p
and	b.nr_sequencia	in (SELECT	x.nr_sequencia
		from	pls_regra_coparticipacao x
		where	x.nr_seq_tipo_coparticipacao = a.nr_sequencia
		and	x.nr_seq_contrato	= nr_seq_contrato_p
		
union

		SELECT	q.nr_sequencia
		from	pls_contrato y,
			pls_contrato_plano w,
			pls_plano	z,
			pls_regra_coparticipacao q
		where	y.nr_sequencia = nr_seq_contrato_p
		and	w.nr_seq_contrato = y.nr_sequencia
		and	w.nr_seq_plano = z.nr_sequencia
		and	q.nr_seq_plano = z.nr_sequencia
		and	q.nr_seq_tipo_coparticipacao = a.nr_sequencia);

return	ie_copartic_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
