CREATE OR REPLACE FUNCTION pls_obter_co_mat_duplic ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, dt_procedimento_p pls_conta_proc.dt_procedimento%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_duplicado_w			varchar(1)	:= 'S';
vl_co_ptu_imp_w			pls_conta_proc.vl_co_ptu_imp%type;
vl_material_ptu_imp_w		pls_conta_proc.vl_material_ptu_imp%type;
vl_procedimento_ptu_imp_w	pls_conta_proc.vl_procedimento_ptu_imp%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.vl_co_ptu_imp,
		a.vl_material_ptu_imp,
		a.vl_procedimento_ptu_imp
	from	pls_conta_proc		a
	where	nr_seq_conta		=  nr_seq_conta_p
	and	nr_sequencia		<> nr_seq_conta_proc_p
	and	cd_procedimento		=  cd_procedimento_p
	and	ie_origem_proced	=  ie_origem_proced_p
	and 	dt_procedimento		=  dt_procedimento_p
	and	((vl_co_ptu_imp  	 >  0)
	or (vl_material_ptu_imp	 >  0)
	or (vl_procedimento_ptu_imp > 0));
BEGIN

for r_C01 in c01 loop
	begin

	select	a.vl_co_ptu_imp,
		a.vl_material_ptu_imp,
		a.vl_procedimento_ptu_imp
	into STRICT 	vl_co_ptu_imp_w,
		vl_material_ptu_imp_w,
		vl_procedimento_ptu_imp_w
	from	pls_conta_proc	a
	where	a.nr_sequencia = nr_seq_conta_proc_p;

	/*se houver cobrança de algum valor em duplicidade gera a ocorrência.*/

	if (coalesce(vl_co_ptu_imp_w,0)	<> 0)	and (coalesce(r_C01.vl_co_ptu_imp,0)	<> 0)	then

		ie_duplicado_w	:= 'N';
		goto final;

	elsif (coalesce(vl_material_ptu_imp_w,0)	<> 0)	and (coalesce(r_C01.vl_material_ptu_imp,0)	<> 0)	then

		ie_duplicado_w	:= 'N';
		goto final;

	elsif (coalesce(vl_procedimento_ptu_imp_w,0) <> 0)	and (coalesce(r_c01.vl_procedimento_ptu_imp,0) <> 0)then

		ie_duplicado_w	:= 'N';
		goto final;

	end if;


	end;
end loop;

<<final>>

return	ie_duplicado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
