CREATE OR REPLACE FUNCTION pls_obter_cpt_usjrp ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_cpt_w		pls_carencia.nr_sequencia%type;
nr_seq_tipo_carencia_w	pls_carencia.nr_seq_tipo_carencia%type;
qt_dias_w		pls_carencia.qt_dias%type;
dt_inicio_vigencia_w	pls_carencia.dt_inicio_vigencia%type;
dt_adesao_benef_w	pls_segurado.dt_inclusao_operadora%type;
qt_registro_w		integer;
ds_retorno_w		varchar(10)	:= '';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_tipo_carencia
	from	pls_carencia
	where	nr_seq_segurado	= nr_seq_segurado_p;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_carencia
where	nr_seq_segurado	= nr_seq_segurado_p
and	substr(pls_obter_se_cpt(nr_seq_tipo_carencia),1,1) = 'S';

select	dt_inclusao_operadora
into STRICT	dt_adesao_benef_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (qt_registro_w > 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_cpt_w,
		nr_seq_tipo_carencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (substr(pls_obter_se_cpt(nr_seq_tipo_carencia_w),1,1) = 'S') then
			select	qt_dias,
				dt_inicio_vigencia
			into STRICT	qt_dias_w,
				dt_inicio_vigencia_w
			from	pls_carencia
			where	nr_sequencia	= nr_seq_cpt_w;

			ds_retorno_w	:= to_char(coalesce(dt_inicio_vigencia_w,dt_adesao_benef_w) + qt_dias_w,'dd/mm/yyyy');
		end if;
		end;
	end loop;
	close C01;
else
	ds_retorno_w	:= 'NAO HA';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
