CREATE OR REPLACE FUNCTION pls_obter_credenciado_partic ( nr_seq_particip_p bigint) RETURNS varchar AS $body$
DECLARE

			 
cd_medico_w		varchar(10);
cd_estabelecimento_w	smallint;
nr_seq_prestador_w	bigint;
ds_retorno_w		varchar(80)	:= '';

/* ESSA FUNCTION É UTILIZADA NO DICIONÁRIO DE DADOS NOS PARTICIPANTES DA FUNÇÃO OPS - CONTAS MÉDICAS */
 
 

BEGIN 
 
select	a.cd_medico, 
	c.cd_estabelecimento 
into STRICT	cd_medico_w, 
	cd_estabelecimento_w 
from	pls_conta		c, 
	pls_conta_proc		b, 
	pls_proc_participante	a 
where	a.nr_seq_conta_proc	= b.nr_sequencia 
and	b.nr_seq_conta		= c.nr_sequencia 
and	a.nr_sequencia		= nr_seq_particip_p;
 
nr_seq_prestador_w	:= pls_obter_credenciado(cd_medico_w, cd_estabelecimento_w);
 
if (nr_seq_prestador_w	> 0) then 
	ds_retorno_w		:= pls_obter_dados_prestador(nr_seq_prestador_w,'N');
	ds_retorno_w	:= ds_retorno_w || ' ('||nr_seq_prestador_w||')';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
