CREATE OR REPLACE FUNCTION pls_obter_cta_princ_atend_imp ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, ie_origem_conta_p out text) AS $body$
DECLARE


/*
	ie_origem_conta_p refere-se onde achou a sequencia de conta principal, se nas tabelas de importacao, ou nas
	tabelas quentes.
*/
					
cd_guia_ref_w			pls_conta.cd_guia_referencia%type;
ie_tipo_guia_w			pls_conta.ie_tipo_guia%type;
nr_retorno_w			pls_conta.nr_sequencia%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
cd_atendimento_w		pls_tipo_atendimento.cd_tiss%type;
ie_regime_atendimento_w	pls_conta_imp.ie_regime_atendimento%type;


BEGIN

select	a.cd_guia_ok_conv cd_guia_referencia,
	a.nr_seq_segurado_conv,	
	b.ie_tipo_guia,
	(	select	max(z.cd_tiss)
		from	pls_tipo_atendimento z
		where	z.nr_sequencia = a.nr_seq_tipo_atend_conv),
	a.ie_regime_atendimento
into STRICT	cd_guia_ref_w,
	nr_seq_segurado_w,	
	ie_tipo_guia_w,
	cd_atendimento_w,
	ie_regime_atendimento_w
from	pls_conta_imp a,
	pls_protocolo_conta_imp b
where	a.nr_sequencia = nr_seq_conta_p
and	a.nr_seq_protocolo = b.nr_sequencia;

-- Se for uma guia de honorario individual entao deve ser buscado a primeira conta de guia de resumo de internacao cadastrada referente a esta guia.
if (ie_tipo_guia_w = 6) then
		
	        select	min(a.nr_sequencia)
		into STRICT	nr_retorno_w
		from	pls_conta_imp a,
			pls_protocolo_conta_imp b
		where	a.cd_guia_ok_conv	= cd_guia_ref_w
		and	a.nr_seq_protocolo 	= b.nr_sequencia
		and 	b.ie_tipo_guia 		= 5
		and	a.nr_seq_segurado_conv	= nr_seq_segurado_w;
		
		if (coalesce(nr_retorno_w::text, '') = '') then
			select	min(a.nr_sequencia),
				min('C')
			into STRICT	nr_retorno_w,
				ie_origem_conta_p
			from	pls_conta a
			where	a.cd_guia_referencia	= cd_guia_ref_w
			and 	a.ie_tipo_guia 		= 5
			and	a.nr_seq_segurado	= nr_seq_segurado_w;	
		end if;
-- Se for uma guia de SP\SADT deve ser verificado o tipo de atendimento. Se for uma conta de SP\SADT gerada dentro de uma guia de internacao, deve

-- ser buscado a primeira conta cadastrada de resumo de internacao para esta guia que esta sendo referenciada. Se for uma conta de SP\SADT que nao estiver

-- em um atendimento de internacao, deve ser buscado a primeira conta de SP\SADT cadastrada para este atendimento.
elsif (ie_tipo_guia_w = 4) then

	-- SADT internado.
	if (cd_atendimento_w = '07' or ie_regime_atendimento_w = '03') then
	
		select	min(a.nr_sequencia)
		into STRICT	nr_retorno_w
		from	pls_conta_imp	a,
			pls_protocolo_conta_imp b
		where	a.nr_seq_protocolo 	= b.nr_sequencia
		and	a.nr_seq_segurado_conv	= nr_seq_segurado_w
		and	a.cd_guia_ok_conv	= cd_guia_ref_w		
		and	b.ie_tipo_guia		= 5;

		if (coalesce(nr_retorno_w::text, '') = '') then
		
			select	min(a.nr_sequencia),
				min('C')
			into STRICT	nr_retorno_w,
				ie_origem_conta_p
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia_referencia	= cd_guia_ref_w		
			and	a.ie_tipo_guia		= 5;

		end if;
		
		if (coalesce(nr_retorno_w::text, '') = '') then
			select	min(a.nr_sequencia),
				min('C')
			into STRICT	nr_retorno_w,
				ie_origem_conta_p
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia_ok		= cd_guia_ref_w		
			and	a.ie_tipo_guia		= 5;
		end if;
	else	
	
		select	min(a.nr_sequencia)
		into STRICT	nr_retorno_w
		from	pls_conta_imp	a,
			pls_protocolo_conta_imp b
		where	a.nr_seq_protocolo = b.nr_sequencia
		and	a.nr_seq_segurado_conv	= nr_seq_segurado_w
		and	a.cd_guia_ok_conv	= cd_guia_ref_w
		and	b.ie_tipo_guia		= 4;
	
		if (coalesce(nr_retorno_w::text, '') = '') then	
			select	min(a.nr_sequencia),
				min('C')
			into STRICT	nr_retorno_w,
				ie_origem_conta_p
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia		= cd_guia_ref_w
			and	a.ie_tipo_guia		= 4;

		end if;
		
		if (coalesce(nr_retorno_w::text, '') = '') then
			select	min(a.nr_sequencia),
				min('C')
			into STRICT	nr_retorno_w,
				ie_origem_conta_p
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia_ok		= cd_guia_ref_w
			and	a.ie_tipo_guia		= 4;	
		end if;
	end if;
end if;

-- Se nao foi encontrado um conta principaal para esta conta entao assume-se que a mesma e a unica conta do atendimento, sendo tambem a principal conta do mesmo.
if (coalesce(nr_retorno_w::text, '') = '') then
	nr_retorno_w := nr_seq_conta_p;
end if;

--Se variavel estiver nula, entao retorna como conta localizada nas tabelas IMP, mesmo que nao

--tenha localizado nada, retornara a propria conta, o que faz sentido retornar I nesses casos.
if (coalesce(ie_origem_conta_p::text, '') = '' ) then
	ie_origem_conta_p := 'I';
end if;

return;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_cta_princ_atend_imp ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, ie_origem_conta_p out text) FROM PUBLIC;

