SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_acionista ( nr_seq_periodo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
	PF
	PJ
	Total	*/
qt_retorno_w			bigint;
qt_acoes_quotas_pj_w		bigint;
qt_acoes_quotas_pf_w		bigint;
qt_total_acoes_w		bigint;


BEGIN
if (ie_opcao_p = 'PJ') then
	select	qt_acoes_quotas
	into STRICT	qt_acoes_quotas_pj_w
	from	w_diops_cad_acion_pf_pj
	where	nr_seq_periodo	= nr_seq_periodo_p
	and	(cd_cgc IS NOT NULL AND cd_cgc::text <> '')
	order by cd_cgc;

	qt_retorno_w	:= qt_acoes_quotas_pj_w;
elsif (ie_opcao_p = 'PF') then
	select	qt_acoes_quotas qt_acoes_quotas_pf
	into STRICT	qt_acoes_quotas_pf_w
	from	w_diops_cad_acion_pf_pj
	where	nr_seq_periodo	= nr_seq_periodo_p
	and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '')
	order by nr_cpf;

	qt_retorno_w	:= qt_acoes_quotas_pf_w;
elsif (ie_opcao_p = 'Total') then
	select	qt_total_acoes
	into STRICT	qt_total_acoes_w
	from  	w_diops_cad_acionquotista
	where 	nr_seq_periodo 	= nr_seq_periodo_p
	order by ie_pulverizado;

	qt_retorno_w	:= qt_total_acoes_w;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
