SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_agravo ( nr_seq_agravo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	D = Descrição do agravo
	VT = Valor do agravo
	P = Parcelas
*/
ds_retorno_w			varchar(255);
ds_agravo_w			varchar(255);


BEGIN

select	ds_agravo
into STRICT	ds_agravo_w
from	pls_agravo
where	nr_sequencia	= nr_seq_agravo_p;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_agravo_w;
elsif (ie_opcao_p	= 'VT') then
	select	max(b.vl_agravo)
	into STRICT	ds_retorno_w
	from	pls_agravo_lote_valor	a,
		pls_agravo_valor	b
	where	b.nr_seq_lote	= a.nr_sequencia
	and	a.nr_seq_agravo	= nr_seq_agravo_p;
elsif (ie_opcao_p	= 'P') then
	select	max(qt_parcelas)
	into STRICT	ds_retorno_w
	from	pls_agravo_lote_valor	a,
		pls_agravo_valor	b
	where	b.nr_seq_lote	= a.nr_sequencia
	and	a.nr_seq_agravo	= nr_seq_agravo_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_agravo ( nr_seq_agravo_p bigint, ie_opcao_p text) FROM PUBLIC;

