CREATE OR REPLACE FUNCTION pls_obter_dados_ame_destino ( nr_seq_regra_destino_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	D	Descrição
*/
nr_seq_ame_subsidiaria_w	pls_ame_subsidiaria.nr_sequencia%type;
nr_seq_ame_empresa_w		pls_ame_empresa.nr_sequencia%type;
ds_retorno_w			varchar(255);


BEGIN

if (ie_opcao_p = 'D') then
	select	max(nr_seq_ame_subsidiaria),
		max(nr_seq_ame_empresa)
	into STRICT	nr_seq_ame_subsidiaria_w,
		nr_seq_ame_empresa_w
	from	pls_ame_regra_ger_dest
	where	nr_sequencia	= nr_seq_regra_destino_p;

	if (nr_seq_ame_subsidiaria_w IS NOT NULL AND nr_seq_ame_subsidiaria_w::text <> '') then
		select	coalesce(nm_interno,obter_razao_social(cd_cgc))
		into STRICT	ds_retorno_w
		from	pls_ame_subsidiaria
		where	nr_sequencia	= nr_seq_ame_subsidiaria_w;
	elsif (nr_seq_ame_empresa_w IS NOT NULL AND nr_seq_ame_empresa_w::text <> '') then
		select	coalesce(nm_interno,obter_razao_social(cd_cgc))
		into STRICT	ds_retorno_w
		from	pls_ame_empresa
		where	nr_sequencia	= nr_seq_ame_empresa_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
