SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_analise_disc ( nr_seq_analise_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(255) := null;
nr_seq_lote_protocolo_w		bigint;
nr_seq_lote_contest_w		bigint;
nr_seq_ptu_fatura_w		bigint;
nr_seq_pls_fatura_w		bigint;


BEGIN
if (ie_opcao_p = 'NEF') then		-- NDC externo
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_nota_credito_debito)
	into STRICT	ds_retorno_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

elsif (ie_opcao_p = 'NEN') then		-- NDC ext NDR
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_nota_credito_debito_ndr)
	into STRICT	ds_retorno_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

elsif (ie_opcao_p = 'NIF') then		-- NDC interno
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_titulo_receber)
	into STRICT	ds_retorno_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

elsif (ie_opcao_p = 'NIN') then		-- NDC interno NDR
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_titulo_receber_ndr)
	into STRICT	ds_retorno_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

elsif (ie_opcao_p = 'FAT') then		-- FATURA
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_seq_lote_contest)
	into STRICT	nr_seq_lote_contest_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

	select	max(nr_seq_ptu_fatura),
		max(nr_seq_pls_fatura)
	into STRICT	nr_seq_ptu_fatura_w,
		nr_seq_pls_fatura_w
	from	pls_lote_contestacao
	where	nr_sequencia = nr_seq_lote_contest_w;

	if (nr_seq_ptu_fatura_w IS NOT NULL AND nr_seq_ptu_fatura_w::text <> '') then
		select	max(nr_fatura)
		into STRICT	ds_retorno_w
		from	ptu_fatura
		where	nr_sequencia = nr_seq_ptu_fatura_w;

	elsif (nr_seq_pls_fatura_w IS NOT NULL AND nr_seq_pls_fatura_w::text <> '') then
		select	coalesce(max(pt.nr_fatura),to_char(max(pf.nr_titulo)))
		into STRICT	ds_retorno_w
		FROM pls_fatura pf
LEFT OUTER JOIN ptu_fatura pt ON (pf.nr_sequencia = pt.nr_seq_pls_fatura)
WHERE pf.nr_sequencia = nr_seq_pls_fatura_w;
	end if;

elsif (ie_opcao_p = 'NDR') then		-- NDR
	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_protocolo_w
	from	pls_analise_conta
	where	nr_sequencia = nr_seq_analise_p;

	select	max(nr_seq_lote_contest)
	into STRICT	nr_seq_lote_contest_w
	from	pls_lote_discussao
	where	nr_seq_lote_conta = nr_seq_lote_protocolo_w;

	select	max(nr_seq_ptu_fatura),
		max(nr_seq_pls_fatura)
	into STRICT	nr_seq_ptu_fatura_w,
		nr_seq_pls_fatura_w
	from	pls_lote_contestacao
	where	nr_sequencia = nr_seq_lote_contest_w;

	if (nr_seq_ptu_fatura_w IS NOT NULL AND nr_seq_ptu_fatura_w::text <> '') then
		select	max(nr_nota_credito_debito)
		into STRICT	ds_retorno_w
		from	ptu_fatura
		where	nr_sequencia = nr_seq_ptu_fatura_w;

	elsif (nr_seq_pls_fatura_w IS NOT NULL AND nr_seq_pls_fatura_w::text <> '') then
		select	coalesce(max(pt.nr_nota_credito_debito),to_char(max(pf.nr_titulo_ndc)))
		into STRICT	ds_retorno_w
		FROM pls_fatura pf
LEFT OUTER JOIN ptu_fatura pt ON (pf.nr_sequencia = pt.nr_seq_pls_fatura)
WHERE pf.nr_sequencia = nr_seq_pls_fatura_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_analise_disc ( nr_seq_analise_p bigint, ie_opcao_p text) FROM PUBLIC;

