CREATE OR REPLACE FUNCTION pls_obter_dados_analise_item ( nr_seq_item_analise_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
IE_OPCAO_P: 
'TI'	tipo item	 
*/
 
 
ds_retorno_w		varchar(255);
ie_tipo_item_w		varchar(255);

			 

BEGIN 
 
begin 
if (coalesce(nr_seq_item_analise_p,0) > 0) then 
	select	ie_tipo_item	 
	into STRICT 	ie_tipo_item_w 
	from 	pls_resumo_conta_v	 
	where	nr_sequencia = nr_seq_item_analise_p;
end if;
exception 
when others then 
	ds_retorno_w	:= null;
end;
 
 
if (ie_opcao_p = 'TI') then 
	ds_retorno_w	:= ie_tipo_item_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
