CREATE OR REPLACE FUNCTION pls_obter_dados_anexo_tiss (nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, ie_tipo_info_p text) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);
nr_seq_segurado_w	varchar(255);

/* ie_opcao_p
	NRS = NÃºmero sequencia segurado
*/
BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	nr_seq_segurado
	into STRICT	nr_seq_segurado_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then

	select	nr_seq_segurado
	into STRICT	nr_seq_segurado_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

end if;

if (ie_tipo_info_p = 'NRS') then
	retorno_w := nr_seq_segurado_w;
end if;


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
