CREATE OR REPLACE FUNCTION pls_obter_dados_atend_evento ( nr_seq_atend_evento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Obter a dados do atendimentos evento 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Opções de retorno: 
 
NB - Nome beneficiário 
ST - Status atendimento 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ 
 
ds_retorno_w	varchar(240);
qt_registro_w	bigint;


BEGIN 
begin 
if (upper(ie_opcao_p) = 'NB') then 
	select 	substr(pls_obter_dados_segurado(a.nr_seq_segurado,'N'),1,255) 
	into STRICT	ds_retorno_w 
	from 	pls_atendimento a, 
		pls_atendimento_evento b, 
		pls_atend_evt_agenda_ext c 
	where 	a.nr_sequencia		= b.nr_seq_atendimento 
	and	b.nr_sequencia		= c.nr_seq_atend_evento 
	and	c.nr_seq_atend_evento 	= nr_seq_atend_evento_p 
	and 	(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '');
	 
elsif (upper(ie_opcao_p) = 'ST') then 
	select	a.ie_status 
	into STRICT	ds_retorno_w 
	from	pls_atendimento a, 
		pls_atendimento_evento b 
	where	a.nr_sequencia 	= b.nr_seq_atendimento 
	and	b.nr_sequencia 	= nr_seq_atend_evento_p;		
end if;
exception 
	when others then 
	ds_retorno_w	:= '';
end;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
