SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_atend_operad ( cd_cgc_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
Obter os dados de atendimento da PJ, cadastrados no complemento, tipo Atendimento 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
PC - Pessoa contato 
FC - Telefone contato 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_retorno_w			varchar(255);
nr_seq_complemento_w		pessoa_juridica_compl.nr_sequencia%type;
nm_pessoa_contato_w		pessoa_juridica_compl.nm_pessoa_contato%type;
nr_ddd_telefone_w		pessoa_juridica_compl.nr_ddd_telefone%type;	
nr_telefone_w			pessoa_juridica_compl.nr_telefone%type;


BEGIN 
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then	 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_complemento_w 
	from  	pessoa_juridica_compl a 
	where 	a.cd_cgc 		= cd_cgc_p 
	and	ie_tipo_complemento	= 6;	
	 
	if (nr_seq_complemento_w IS NOT NULL AND nr_seq_complemento_w::text <> '') then 
		begin 
			select	nm_pessoa_contato, 
				nr_ddd_telefone, 
				nr_telefone 
			into STRICT	nm_pessoa_contato_w, 
				nr_ddd_telefone_w, 
				nr_telefone_w 
			from	pessoa_juridica_compl 
			where 	nr_sequencia	= nr_seq_complemento_w 
			and	cd_cgc 		= cd_cgc_p;
		exception 
		when others then 
			nm_pessoa_contato_w	:= null;
			nr_ddd_telefone_w	:= null;
			nr_telefone_w		:= null;
		end;
		 
		if (ie_opcao_p = 'PC') then 
			ds_retorno_w	:= nm_pessoa_contato_w;
		elsif (ie_opcao_p = 'FC') then 
			if (nr_ddd_telefone_w IS NOT NULL AND nr_ddd_telefone_w::text <> '' AND nr_telefone_w IS NOT NULL AND nr_telefone_w::text <> '') then 
				ds_retorno_w	:= nr_ddd_telefone_w||' '||nr_telefone_w;
			else 
				ds_retorno_w	:= nr_telefone_w;
			end if;			
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_atend_operad ( cd_cgc_p text, ie_opcao_p text) FROM PUBLIC;

