SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_auditor ( cd_ocorrencia_p text, nr_seq_glosa_p bigint, nm_usuario_p text, ie_tipo_p text, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


/*	ie_tipo_p
	L - Nivel de liberação (Retorna nivel do auditor no grupo)
	A - Se auditor  grupo(Retorna S para Sim e N para Não)
	C - Se auditor ocorrência
		Retorna T Se não possue grupo na ocorrencia
		Retorna S Se o auditor, neste grupo, é parte do grupo de análise da ocorrência
		Retorna N Se não faz parte
*/
ds_retorno_w		varchar(255);
nr_seq_nivel_lib_w	bigint;
ie_permissao_w		varchar(2);
nr_seq_ocorrencia_w	bigint;
qt_grupo_w		bigint;


BEGIN

if (ie_tipo_p = 'L') then


	select	max(coalesce(a.nr_seq_nivel_lib,b.nr_seq_nivel_lib))
	into STRICT	nr_seq_nivel_lib_w
	from	pls_membro_grupo_aud a,
		pls_grupo_auditor b
	where	a.nr_seq_grupo = nr_seq_grupo_p
	and	a.nm_usuario_exec = nm_usuario_p
	and	a.nr_seq_grupo = b.nr_sequencia;

	if (nr_seq_nivel_lib_w IS NOT NULL AND nr_seq_nivel_lib_w::text <> '') then

		begin
		select	nr_nivel_liberacao
		into STRICT	ds_retorno_w
		from	pls_nivel_liberacao
		where	nr_sequencia = nr_seq_nivel_lib_w;
		exception
		when others then
			ds_retorno_w := null;
		end;

		goto final;
	end if;

elsif (ie_tipo_p = 'A') then

	select	max(ie_permissao)
	into STRICT	ie_permissao_w
	from	pls_grupo_auditor
	where	nr_sequencia = nr_seq_grupo_p;

	/*Se o usuário for master então é retornado que este faz pasrte do grupo de análise*/

	if (ie_permissao_w in ('T','R')) then -- tratamento realizado para o tipo de permissão Acesso total com restrições  - Demitrius  07/02/2011
		ds_retorno_w := 'S';
	else
		begin
		select	nr_sequencia
		into STRICT	ds_retorno_w
		from	pls_membro_grupo_aud
		where	nr_seq_grupo = nr_seq_grupo_p
		and	nm_usuario_exec = nm_usuario_p
		and	ie_situacao = 'A';
		exception
		when others then
			ds_retorno_w := null;
		end;

		/*Se encontrou um registro não é necessário continuar*/

		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := 'S';
			goto final;
		end if;

		ds_retorno_w := 'N';
	end if;

elsif (ie_tipo_p = 'C') then

	select	max(ie_permissao)
	into STRICT	ie_permissao_w
	from	pls_grupo_auditor
	where	nr_sequencia = nr_seq_grupo_p;

	if (ie_permissao_w in ('T','R')) then
		ds_retorno_w := 'S';
		goto final;
	end if;

	select	max(nr_sequencia)
	into STRICT	nr_seq_ocorrencia_w
	from	pls_ocorrencia
	where	cd_ocorrencia = cd_ocorrencia_p;

	select  count(1)
	into STRICT	qt_grupo_w
	from 	pls_ocorrencia_grupo
	where 	nr_seq_ocorrencia = nr_seq_ocorrencia_w
	and	ie_situacao = 'A';

	if (qt_grupo_w = 0) then
		ds_retorno_w := 'T';
	else
		select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	pls_membro_grupo_aud a,
			pls_ocorrencia_grupo b
		where	a.nr_seq_grupo			= b.nr_seq_grupo
		and	coalesce(a.nm_usuario_exec,'X') 	= nm_usuario_p
		and	a.nr_seq_grupo		 	= nr_seq_grupo_p
		and 	b.nr_seq_ocorrencia 		= nr_seq_ocorrencia_w
		and	b.ie_situacao = 'A'
		and	a.ie_situacao = 'A';
	end if;

end if;

<<final>>
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_auditor ( cd_ocorrencia_p text, nr_seq_glosa_p bigint, nm_usuario_p text, ie_tipo_p text, nr_seq_grupo_p bigint) FROM PUBLIC;

