SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_banco_prest ( nr_seq_prestador_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(30);
ds_banco_w		varchar(30);
cd_banco_w		varchar(30);
cd_agencia_bancaria_w	varchar(30);
nr_conta_w		varchar(20);
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(10);
qt_dados_w		bigint;

BEGIN
/* ie_opcao_p
     B = CD_BANCO
     DS = DESCRICAO BANC O(DS_BANCO)
     A = CD_AGENCIA_BANCARIA
     C = NR_CONTA
*/
select 	count(1)
into STRICT	qt_dados_w
from 	pls_prestador_pagto
where	nr_seq_prestador = nr_seq_prestador_p;

if (qt_dados_w > 0)	then
	select  cd_banco,
		substr(obter_nome_banco(cd_banco),1,30)ds_banco,
		cd_agencia_bancaria,
		nr_conta
	into STRICT 	cd_banco_w,
		ds_banco_w,
		cd_agencia_bancaria_w,
		nr_conta_w
	from 	pls_prestador_pagto
	where	nr_seq_prestador  = nr_seq_prestador_p
	and	clock_timestamp() between dt_inicio_vigencia_ref and dt_fim_vigencia_ref  LIMIT 1;

	goto final;
end if;

begin
select 	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from 	pls_prestador
where 	nr_sequencia = nr_seq_prestador_p;
exception
when others then
	cd_pessoa_fisica_w	:= '';
end;

if (cd_pessoa_fisica_w <> '')	then
	select  cd_banco,
		substr(obter_nome_banco(cd_banco),1,30)ds_banco,
		cd_agencia_bancaria,
		nr_conta
	into STRICT 	cd_banco_w,
		ds_banco_w,
		cd_agencia_bancaria_w,
		nr_conta_w
	from 	pessoa_fisica_conta
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	goto final;
end if;

begin
select 	cd_cgc
into STRICT	cd_cgc_w
from 	pls_prestador
where 	nr_sequencia = nr_seq_prestador_p;
exception
when others then
	cd_cgc_w	:= '';
end;

if (cd_cgc_w <> '')	then
	select  cd_banco,
		substr(obter_nome_banco(cd_banco),1,30)ds_banco,
		cd_agencia_bancaria,
		nr_conta
	into STRICT 	cd_banco_w,
		ds_banco_w,
		cd_agencia_bancaria_w,
		nr_conta_w
	from 	pessoa_juridica_conta
	where	cd_cgc = cd_cgc_w;

	goto final;
end if;

<<final>>

if (ie_opcao_p = 'B')	then
	ds_retorno_w	:= cd_banco_w;
elsif (ie_opcao_p = 'DS')	then
	ds_retorno_w	:= ds_banco_w;
elsif (ie_opcao_p = 'A')	then
	ds_retorno_w	:= cd_agencia_bancaria_w;
elsif (ie_opcao_p = 'C')	then
	ds_retorno_w	:= nr_conta_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_banco_prest ( nr_seq_prestador_p bigint, ie_opcao_p text) FROM PUBLIC;

