SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_benef_ds ( nr_seq_proposta_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	N - Nome beneficiario
	NT - Nome titular
	DA - Data análise de adesão
	UA - Usuário que encaminhou para análise
	GP - Grau parentesco
*/
ds_retorno_w			varchar(255);
nm_beneficiario_w		varchar(255);
nm_usuario_analise_w		varchar(15);
nr_seq_titular_w		bigint;
nr_seq_beneficiario_w		bigint;
nr_seq_parentesco_w		bigint;
dt_analise_w			timestamp;


BEGIN

select	b.nm_pessoa_fisica,
	coalesce(a.nr_seq_titular,0),
	a.nr_sequencia,
	a.nr_seq_parentesco
into STRICT	nm_beneficiario_w,
	nr_seq_titular_w,
	nr_seq_beneficiario_w,
	nr_seq_parentesco_w
from	pls_proposta_beneficiario	a,
	pessoa_fisica			b
where	a.cd_beneficiario	= b.cd_pessoa_fisica
and	a.nr_seq_proposta	= nr_seq_proposta_p
and	a.cd_beneficiario	= cd_pessoa_fisica_p;

begin
select	dt_analise,
	nm_usuario_analise
into STRICT	dt_analise_w,
	nm_usuario_analise_w
from	pls_analise_adesao
where	nr_seq_pessoa_proposta	= nr_seq_beneficiario_w;
exception
	when others then
	dt_analise_w		:= null;
	nm_usuario_analise_w	:= '';
end;

if (ie_opcao_p	= 'N') then
	ds_retorno_w	:= nm_beneficiario_w;
elsif (ie_opcao_p	= 'NT') and (nr_seq_titular_w > 0) then
	select	b.nm_pessoa_fisica
	into STRICT	ds_retorno_w
	from	pls_proposta_beneficiario	a,
		pessoa_fisica			b
	where	a.cd_beneficiario	= b.cd_pessoa_fisica
	and	nr_sequencia		= nr_seq_titular_w;
elsif (ie_opcao_p	= 'DA') then
	ds_retorno_w	:= dt_analise_w;
elsif (ie_opcao_p	= 'UA') then
	ds_retorno_w	:= nm_usuario_analise_w;
elsif (ie_opcao_p	= 'GP') and (nr_seq_parentesco_w > 0) then
	select	ds_parentesco
	into STRICT	ds_retorno_w
	from	grau_parentesco
	where	nr_sequencia	= nr_seq_parentesco_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
