CREATE OR REPLACE FUNCTION pls_obter_dados_benef_lote ( nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
DTC  - Data da contratação do lote
*/
ds_retorno_w		varchar(255)	:= '';
dt_contratacao_w	varchar(10);


BEGIN

select	to_char(dt_contratacao, 'dd/mm/yyyy') dt
into STRICT	dt_contratacao_w
from	pls_lote_inclusao_benef
where	nr_sequencia	= nr_seq_lote_p;


if (ie_opcao_p = 'DTC') then
	ds_retorno_w := dt_contratacao_w;
end if;


return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
