SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_benef_prop ( nr_seq_beneficiario_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	'N' = Nome beneficiario
	'NT' = Nome titular
	'DA' = Data analise de adesao
	'UA' = Usuario que encaminhou para analise
	'TC' = Tipo de contratacao da proposta
*/
ds_retorno_w			varchar(255);
nm_beneficiario_w		varchar(255);
nr_seq_titular_w		bigint;
dt_analise_w			timestamp;
nm_usuario_analise_w		varchar(15);
qt_registros_w			bigint;
nr_seq_proposta_w		bigint;


BEGIN

select	substr(obter_nome_pf(cd_beneficiario),1,255),
	coalesce(nr_seq_titular,0),
	nr_seq_proposta
into STRICT	nm_beneficiario_w,
	nr_seq_titular_w,
	nr_seq_proposta_w
from	pls_proposta_beneficiario
where	nr_sequencia = nr_seq_beneficiario_p;


select	count(*)
into STRICT	qt_registros_w
from	pls_analise_adesao		b,
	pls_proposta_beneficiario	a
where	b.nr_seq_pessoa_proposta	= a.nr_sequencia
and	a.nr_sequencia			= nr_seq_beneficiario_p;

if (qt_registros_w > 0) then
	begin
	select	dt_analise,
		nm_usuario_analise
	into STRICT	dt_analise_w,
		nm_usuario_analise_w
	from	pls_analise_adesao
	where	nr_seq_pessoa_proposta	= nr_seq_beneficiario_p;
	exception
		when others then
		dt_analise_w		:= null;
		nm_usuario_analise_w	:= '';
	end;
end if;

if (ie_opcao_p	= 'N') then
	ds_retorno_w	:= nm_beneficiario_w;
elsif (ie_opcao_p	= 'NT') and (nr_seq_titular_w > 0) then
	select	substr(obter_nome_pf(cd_beneficiario),1,255)
	into STRICT	ds_retorno_w
	from	pls_proposta_beneficiario
	where	nr_sequencia = nr_seq_titular_w;
elsif (ie_opcao_p	= 'DA') then
	ds_retorno_w	:= to_char(dt_analise_w,'dd/mm/yyyy');
elsif (ie_opcao_p	= 'UA') then
	ds_retorno_w	:= nm_usuario_analise_w;
elsif (ie_opcao_p	= 'TC') then
	select	obter_valor_dominio(1666,ie_tipo_contratacao)
	into STRICT	ds_retorno_w
	from	pls_proposta_adesao
	where	nr_sequencia	= nr_seq_proposta_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_benef_prop ( nr_seq_beneficiario_p bigint, ie_opcao_p text) FROM PUBLIC;

