CREATE OR REPLACE FUNCTION pls_obter_dados_benef_susp ( nr_seq_notific_pagador_p pls_notificacao_pagador.nr_sequencia%type, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

-- V - Valor
-- Q - Quantidade
vl_retorno_w			double precision := 0;
nr_seq_preco_w			pls_segurado_preco.nr_sequencia%type;
vl_preco_atual_w		pls_segurado_preco.vl_preco_atual%type;

c01 CURSOR(	nr_seq_notific_pagador_pc	pls_notificacao_pagador.nr_sequencia%type)	FOR
	SELECT	nr_sequencia
	from	pls_segurado	a
	where	a.ie_situacao_atend	= 'S'
	and	a.nr_seq_notific_pagador	= nr_seq_notific_pagador_pc;
BEGIN
if (ie_opcao_p	= 'V') then
	for r_C01_w in C01(nr_seq_notific_pagador_p) loop
		select	max(x.nr_sequencia)
		into STRICT	nr_seq_preco_w
		from	pls_segurado_preco x
		where	x.nr_seq_segurado = r_C01_w.nr_sequencia;

		vl_preco_atual_w := 0;
		if (nr_seq_preco_w IS NOT NULL AND nr_seq_preco_w::text <> '') then
			select	coalesce(x.vl_preco_atual,0)
			into STRICT	vl_preco_atual_w
			from	pls_segurado_preco x
			where	x.nr_sequencia	= nr_seq_preco_w;
		end if;

		vl_retorno_w	:= vl_retorno_w + vl_preco_atual_w;
	end loop;

elsif (ie_opcao_p	= 'Q') then
	for r_C01_w in C01(nr_seq_notific_pagador_p) loop
		vl_retorno_w := vl_retorno_w + 1;
	end loop;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
