CREATE OR REPLACE FUNCTION pls_obter_dados_bonificacao (nr_seq_bonificacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
	D = Descrição
	I = Início Vigência
	F = Fim vigência
	T = Taxa
	V = Valor bonificação
*/
ds_retorno_w			varchar(300);
ds_bonificacao_w		varchar(300);
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w		timestamp;
tx_bonificacao_w		bigint;
vl_bonificacao_w		bigint;


BEGIN
select	substr(pls_obter_desc_bonificacao(nr_seq_bonificacao),1,255),
		dt_inicio_vigencia,
		dt_fim_vigencia,
		tx_bonificacao,
		vl_bonificacao
into STRICT	ds_bonificacao_w,
		dt_inicio_vigencia_w,
		dt_fim_vigencia_w,
		tx_bonificacao_w,
		vl_bonificacao_w
from	pls_bonificacao_vinculo
where	nr_sequencia = nr_seq_bonificacao_p;
if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_bonificacao_w;
elsif (ie_opcao_p = 'I') then
		ds_retorno_w	:= dt_inicio_vigencia_w;
elsif (ie_opcao_p = 'F') then
		ds_retorno_w	:= dt_fim_vigencia_w;
elsif (ie_opcao_p = 'T') then
		ds_retorno_w	:= tx_bonificacao_w;
elsif (ie_opcao_p = 'V') then
		ds_retorno_w	:= vl_bonificacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
