CREATE OR REPLACE FUNCTION pls_obter_dados_camara_contest (nr_seq_lote_contest_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (upper(ie_opcao_p) = 'DE') then -- Data de postagem de arquivo
	select	to_char(max(dt_postagem_arquivo),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	ptu_camara_contestacao
	where	nr_seq_lote_contest = nr_seq_lote_contest_p;

elsif (upper(ie_opcao_p) = 'DU') then -- Descrição de usuário postagem
	select	max(nm_usuario_envio)
	into STRICT	ds_retorno_w
	from	ptu_camara_contestacao
	where	nr_seq_lote_contest = nr_seq_lote_contest_p;

elsif (upper(ie_opcao_p) = 'NU') then -- Nome do usuário postagem
	select	substr(obter_nome_usuario(max(nm_usuario_envio)),1,255)
	into STRICT	ds_retorno_w
	from	ptu_camara_contestacao
	where	nr_seq_lote_contest = nr_seq_lote_contest_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
