SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_carencia_web ( nr_seq_carencia_p bigint, nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	DL - Data de liberação
	N   - Nome
	PN - Data de liberação > que a data atual
*/
ds_retorno_w		varchar(255);
dt_inclusao_w		timestamp;
dt_liberacao_w		timestamp;
ie_insenta_carencia_w	varchar(2);
nr_seq_tipo_carencia_w	bigint;
nr_seq_grupo_carencia_w	bigint;


BEGIN

ds_retorno_w	:= '';

begin
select	coalesce(a.dt_inclusao_operadora,a.dt_contratacao)
into STRICT	dt_inclusao_w
from	pls_segurado a
where	a.nr_sequencia		= nr_seq_segurado_p;
exception
when others then
	dt_inclusao_w	:= null;
end;

select (coalesce(dt_inicio_vigencia,dt_inclusao_w) + qt_dias),
	coalesce(ie_isencao_carencia,'N'),
	nr_seq_tipo_carencia,
	nr_seq_grupo_carencia
into STRICT	dt_liberacao_w,
	ie_insenta_carencia_w,
	nr_seq_tipo_carencia_w,
	nr_seq_grupo_carencia_w
from	pls_carencia
where	nr_sequencia	= nr_seq_carencia_p;

if (ie_opcao_p = 'DT') then
	if (ie_insenta_carencia_w = 'N') then
		ds_retorno_w 	:= to_char(dt_liberacao_w,'dd/mm/yyyy');
	elsif (ie_insenta_carencia_w = 'S') then
		ds_retorno_w	:= to_char(dt_inclusao_w,'dd/mm/yyyy');
	end if;
elsif (ie_opcao_p = 'PN') then
	if (ie_insenta_carencia_w = 'N' and dt_liberacao_w > clock_timestamp()) then
		ds_retorno_w 	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 'N') then
	if (coalesce(nr_seq_tipo_carencia_w,0) <> 0) then
		select	substr(ds_carencia,1,255)
		into STRICT	ds_retorno_w
		from	pls_tipo_carencia
		where	nr_sequencia = nr_seq_tipo_carencia_w;
	elsif (coalesce(nr_seq_grupo_carencia_w,0) <> 0) then
		select	ds_grupo
		into STRICT	ds_retorno_w
		from	pls_grupo_carencia
		where	nr_sequencia	= nr_seq_grupo_carencia_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
