CREATE OR REPLACE FUNCTION pls_obter_dados_cart_segurado (nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	C - CÃ³digo da carteira
	V - Validade da carteira	*/
nr_seq_carteira_w		bigint;
dt_validade_w			timestamp;
cd_usuario_plano_w		varchar(30)	:= '';
ds_retorno_w			varchar(255)	:= '';


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_carteira_w
from	pls_segurado_carteira
where	nr_seq_segurado			= nr_seq_segurado_p
and	coalesce(dt_validade_carteira,clock_timestamp()) >= trunc(clock_timestamp());

if (nr_seq_carteira_w > 0) then
	select	cd_usuario_plano,
		dt_validade_carteira
	into STRICT	cd_usuario_plano_w,
		dt_validade_w
	from	pls_segurado_carteira
	where	nr_sequencia	= nr_seq_carteira_w;

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_usuario_plano_w;
	elsif (ie_opcao_p = 'V') then
		ds_retorno_w	:= to_char(dt_validade_w,'dd/mm/yyyy');
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
