SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_cd_prest_web ( cd_prestador_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= null;
nr_seq_prestador_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(50);

/*
 Tipos
	 NM  - Nome prestador
	SQ - SequÃªncia
*/
BEGIN

select	max(nr_sequencia),
	max(cd_cgc),
	max(cd_pessoa_fisica)
into STRICT	nr_seq_prestador_w,
	cd_cgc_w,
	cd_pessoa_fisica_w
from	pls_prestador
where	cd_prestador = cd_prestador_p;

if (ie_opcao_p = 'NM') then
	select	max(a.nm_interno)
	into STRICT	ds_retorno_w
	from	pls_prestador a
	where	nr_sequencia	= nr_seq_prestador_w;

	if (coalesce(ds_retorno_w::text, '') = '') then
		if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '')	then
			select	ds_razao_social
			into STRICT	ds_retorno_w
			from	pessoa_juridica
			where	cd_cgc	= cd_cgc_w;
		elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')	then
			select	nm_pessoa_fisica
			into STRICT	ds_retorno_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		end if;
	end if;
elsif (ie_opcao_p = 'SQ') then
	ds_retorno_w := nr_seq_prestador_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
