SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_censo ( nr_seq_segurado_p bigint, dt_mes_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


--	Procedure que retorna se o segurado foi incluído/excluído/migrado/ativo no mês de referencia
/*ie_opcao_p
QI - Se segurado inclusão
QE - Se segurado exclusão
QM - Se segurado migrado
QIS - Se incluso no SIB
QES - Se escluso SIB
QAS - Se alterado SIB
A - Se segurado ativo
*/
vl_retorno_w		bigint;


BEGIN

if (ie_opcao_p	= 'QI') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	last_day(dt_contratacao) = dt_mes_referencia_p
	and	nr_sequencia	= nr_seq_segurado_p;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'QE') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	last_day(dt_rescisao) = dt_mes_referencia_p
	and	coalesce(dt_migracao::text, '') = ''
	and	nr_sequencia	= nr_seq_segurado_p;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'QM') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	last_day(dt_migracao) = dt_mes_referencia_p
	and	(dt_rescisao IS NOT NULL AND dt_rescisao::text <> '')
	and	nr_sequencia	= nr_seq_segurado_p;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'QIS') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_interf_sib a,
		pls_lote_sib b
	where	a.nr_seq_lote_sib	= b.nr_sequencia
	and	last_day(b.dt_mes_competencia) = dt_mes_referencia_p
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.ie_tipo_reg	= 1;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'QES') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_interf_sib a,
		pls_lote_sib b
	where	a.nr_seq_lote_sib	= b.nr_sequencia
	and	last_day(b.dt_mes_competencia) = dt_mes_referencia_p
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.ie_tipo_reg	= 3;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'QAS') then
	begin
	select	1
	into STRICT	vl_retorno_w
	from	pls_interf_sib a,
		pls_lote_sib b
	where	a.nr_seq_lote_sib	= b.nr_sequencia
	and	last_day(b.dt_mes_competencia) = dt_mes_referencia_p
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.ie_tipo_reg	= 2;
	exception
	when others then
		vl_retorno_w := 0;
	end;
elsif (ie_opcao_p	= 'A') then
	begin
	select	count(*)
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	dt_mes_referencia_p between trunc(dt_contratacao,'month') and trunc(coalesce(dt_rescisao,clock_timestamp()),'month');
	exception
	when others then
		vl_retorno_w := 0;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
