CREATE OR REPLACE FUNCTION pls_obter_dados_cheque_bordero (nr_seq_cheque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := null;
nr_titulo_w		bigint;
nr_bordero_w		bigint;
nr_bordero_ww		bigint := 0;
ds_virgula_w		varchar(2) := null;

C01 CURSOR FOR
	SELECT	nr_titulo,
		nr_bordero
	from	cheque_bordero_titulo
	where	nr_seq_cheque = nr_seq_cheque_p
	order by 1;


BEGIN
if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_titulo_w,
		nr_bordero_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_opcao_p = 'NT') then
			ds_retorno_w := ds_retorno_w || ', ' || nr_titulo_w;
		end if;

		if (ie_opcao_p = 'NB') then
			if (nr_bordero_ww <> nr_bordero_w) then
				ds_retorno_w := ds_retorno_w || ', ' || nr_bordero_w;
			end if;
			nr_bordero_ww := nr_bordero_w;
		end if;
		end;
	end loop;
	close C01;

	ds_retorno_w :=  substr(ds_retorno_w,3,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
