SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_cid_req ( nr_seq_requisicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: obter os CID's/tempo CID da requisição
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p = 'C' - Trazer todos os CID's da requisicao
ie_opcao_p = 'T' - Trazer o tempo de todos os CID's da requisicao
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);

C01 CURSOR FOR
	SELECT	cd_doenca
	from	pls_requisicao_diagnostico
	where	nr_seq_requisicao = nr_seq_requisicao_p
	and	(cd_doenca IS NOT NULL AND cd_doenca::text <> '');

C02 CURSOR FOR
	SELECT	'CID '||cd_doenca||': '||coalesce(qt_tempo, 0) || ' - ' ||CASE WHEN ie_unidade_tempo='M' THEN  'Meses' WHEN ie_unidade_tempo='A' THEN  'Anos'  ELSE 'Dias' END  ds_cid_tempo
	from	pls_requisicao_diagnostico
	where	nr_seq_requisicao = nr_seq_requisicao_p
	and	(cd_doenca IS NOT NULL AND cd_doenca::text <> '');
BEGIN
	if (ie_opcao_p = 'C') then
		for r_C01_w in C01 loop
			begin
				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w := r_C01_w.cd_doenca;
				else
					ds_retorno_w := ds_retorno_w|| ', '||r_C01_w.cd_doenca;
				end if;
			end;
		end loop;
	elsif (ie_opcao_p = 'T') then
		for r_C02_w in C02 loop
			begin
				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w := r_C02_w.ds_cid_tempo;
				else
					ds_retorno_w := ds_retorno_w|| ', '||r_C02_w.ds_cid_tempo;
				end if;
			end;
		end loop;
	end if;

	return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_cid_req ( nr_seq_requisicao_p bigint, ie_opcao_p text) FROM PUBLIC;

