SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_cliente ( nr_seq_cliente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*	ie_opcao_p 
	D = Nome cliente 
	PA = Proposta de adesão gerada 
	V = Canal de vendas 
	DH = Data do último histórico 
*/
 
 
ds_retorno_w			varchar(255);
nm_cliente_w			varchar(255);
nr_seq_proposta_w		bigint;
nr_seq_canal_w			bigint;
dt_historico_w			timestamp;


BEGIN 
 
if (ie_opcao_p	= 'D') then 
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) 
	into STRICT	ds_retorno_w 
	from	pls_comercial_cliente 
	where	nr_sequencia	= nr_seq_cliente_p;
elsif (ie_opcao_p	= 'PA') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_proposta_w 
	from	pls_proposta_adesao 
	where	nr_seq_cliente	= nr_seq_cliente_p;
	ds_retorno_w	:= to_char(nr_seq_proposta_w);
elsif (ie_opcao_p	= 'V') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_canal_w 
	from	pls_solicitacao_vendedor 
	where	nr_seq_cliente	= nr_seq_cliente_p 
	and	coalesce(dt_fim_vigencia::text, '') = ''  LIMIT 1;
	 
	if (coalesce(nr_seq_canal_w,0) <> 0) then 
		select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_solicitacao_vendedor a, 
			pls_vendedor b 
		where	b.nr_sequencia	= a.nr_seq_vendedor_canal 
		and	a.nr_sequencia	= nr_seq_canal_w;
	end if;
elsif (ie_opcao_p	= 'DH') then 
	select	max(dt_historico) 
	into STRICT	dt_historico_w 
	from	pls_comercial_historico 
	where	nr_seq_cliente	= nr_seq_cliente_p;
	 
	if (dt_historico_w IS NOT NULL AND dt_historico_w::text <> '') then 
		ds_retorno_w	:= to_char(dt_historico_w,'dd/mm/yyyy');
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_cliente ( nr_seq_cliente_p bigint, ie_opcao_p text) FROM PUBLIC;

