CREATE OR REPLACE FUNCTION pls_obter_dados_comercial ( nr_seq_solicitacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
D - Descrição
C - Código
*/
ds_retorno_ww			varchar(255);
ie_retorno_w			varchar(1);
ds_retorno_w			varchar(255);
nr_seq_solicitacao_w		bigint;
nr_seq_cliente_w		bigint;
nr_seq_proposta_w		bigint;
is_status_comercial_w		varchar(255);
ie_etapa_solicitacao_w		varchar(255);


BEGIN

select	max(a.nr_sequencia),
	max(b.nr_sequencia),
	max(c.nr_sequencia)
into STRICT	nr_seq_solicitacao_w,
	nr_seq_cliente_w,
	nr_seq_proposta_w
FROM pls_solicitacao_comercial a
LEFT OUTER JOIN pls_comercial_cliente b ON (a.nr_sequencia = b.nr_seq_solicitacao)
LEFT OUTER JOIN pls_proposta_adesao c ON (b.nr_sequencia = c.nr_seq_cliente)
WHERE a.nr_sequencia	= nr_seq_solicitacao_p;

if (nr_seq_proposta_w <> 0) then
	ie_retorno_w	:= 'P';
	ds_retorno_ww	:= 'Proposta';
elsif (nr_seq_cliente_w <> 0) then
	ie_retorno_w	:= 'C';
	ds_retorno_ww	:= 'Prospecção';
elsif (nr_seq_solicitacao_w <> 0) then
	ie_retorno_w	:= 'S';
	ds_retorno_ww	:= 'Solicitação';
end if;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_retorno_ww;
elsif (ie_opcao_p	= 'C') then
	ds_retorno_w	:= ie_retorno_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
