CREATE OR REPLACE FUNCTION pls_obter_dados_compl_web ( nr_seq_prestador_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

/*
ie_tipo_p:
	NM = Nome Prestador
*/
BEGIN

if (ie_tipo_p = 'NM') then
	if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		select	ds_razao_social
		into STRICT	ds_retorno_w
		from	pessoa_juridica
		where	cd_cgc	= cd_cgc_p;
	elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	nm_pessoa_fisica
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
