SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_conselho (nr_seq_conselho_p conselho_profissional.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);
ds_conselho_w		conselho_profissional.ds_conselho%type;
sg_conselho_w		conselho_profissional.sg_conselho%type;

/*IE_OPCAO_P
	DS - Descrição do conselho
	SG - Sigla do conselho
*/
BEGIN

retorno_w := '';

if (nr_seq_conselho_p IS NOT NULL AND nr_seq_conselho_p::text <> '') then

	select	ds_conselho,
		sg_conselho
	into STRICT	ds_conselho_w,
		sg_conselho_w
	from	conselho_profissional
	where	nr_sequencia = nr_seq_conselho_p;

	if (ie_opcao_p = 'DS') then
		retorno_w := ds_conselho_w;
	elsif (ie_opcao_p = 'SG') then
		retorno_w := sg_conselho_w;
	end if;

end if;


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
