CREATE OR REPLACE FUNCTION pls_obter_dados_conta_item_imp ( nr_seq_conta_item_p pls_conta_item_imp.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
Retorna as informações necessárias da tabela PLS_CONTA_ITEM_IMP

Opções:
VA - Via de acesso
TB - Tipo tabela
*/
ds_retorno_w	varchar(255);


BEGIN
ds_retorno_w	:= null;

if (nr_seq_conta_item_p IS NOT NULL AND nr_seq_conta_item_p::text <> '') then
	if (ie_opcao_p = 'VA') then
		select	max(ie_via_acesso_conv)
		into STRICT	ds_retorno_w
		from	pls_conta_item_imp
		where	nr_sequencia	= nr_seq_conta_item_p;
	elsif (ie_opcao_p = 'TB') then
		select	max(cd_tipo_tabela_conv)
		into STRICT	ds_retorno_w
		from	pls_conta_item_imp
		where	nr_sequencia	= nr_seq_conta_item_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
