CREATE OR REPLACE FUNCTION pls_obter_dados_contestacao ( nr_seq_contestacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(255) := null;
nr_seq_conta_w		bigint;
					

BEGIN 
if (ie_opcao_p = 'NC') then 
	select	nr_seq_conta 
	into STRICT	ds_retorno_w 
	from	pls_contestacao 
	where	nr_sequencia = nr_seq_contestacao_p;
 
elsif (ie_opcao_p = 'NB') then 
	select	substr(pls_obter_dados_conta(nr_seq_conta,'NB'),1,255) nm_beneficiario 
	into STRICT	ds_retorno_w 
	from	pls_contestacao 
	where	nr_sequencia = nr_seq_contestacao_p;
 
elsif (ie_opcao_p = 'NN') then 
	select	max(nr_nota), 
		max(nr_seq_conta) 
	into STRICT	ds_retorno_w, 
		nr_seq_conta_w 
	from	pls_contestacao 
	where	nr_sequencia = nr_seq_contestacao_p;
	 
	if (coalesce(ds_retorno_w::text, '') = '') then 
		select	max(x.nr_nota) 
		into STRICT	ds_retorno_w 
		from	pls_conta y, 
			ptu_nota_cobranca x 
		where 	x.nr_sequencia = y.nr_seq_nota_cobranca 
		and	y.nr_sequencia = nr_seq_conta_w;
	end if;	
	 
elsif (ie_opcao_p = 'NBP') then 
	select	substr(pls_obter_dados_conta(nr_seq_conta,'NBP'),1,255) nm_beneficiario 
	into STRICT	ds_retorno_w 
	from	pls_contestacao 
	where	nr_sequencia = nr_seq_contestacao_p;
	 
elsif (ie_opcao_p = 'CR') then 
	select	max(nr_seq_conta) 
	into STRICT	nr_seq_conta_w 
	from	pls_contestacao 
	where	nr_sequencia = nr_seq_contestacao_p;
	 
	if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then 
		select	substr(pls_obter_dados_segurado(nr_seq_segurado,'CR'),1,255) cd_beneficiario 
		into STRICT	ds_retorno_w 
		from	pls_conta 
		where	nr_sequencia = nr_seq_conta_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
