CREATE OR REPLACE FUNCTION pls_obter_dados_contest_discus ( nr_seq_discussao_p pls_contestacao_discussao.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
nr_seq_conta_w			pls_conta.nr_sequencia%type;
nr_seq_lote_disc_w		pls_lote_discussao.nr_sequencia%type;
					

BEGIN 
if (upper(ie_opcao_p) = 'LF') then 
	select	substr(pls_obter_dados_contestacao(nr_seq_contestacao,'NC'),1,10), 
		nr_seq_lote 
	into STRICT	nr_seq_conta_w, 
		nr_seq_lote_disc_w 
	from	pls_contestacao_discussao 
	where	nr_sequencia	= nr_seq_discussao_p;
	 
	if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then 
		select	max(a.nr_sequencia) 
		into STRICT	ds_retorno_w 
		from	pls_ajuste_fatura_conta a, 
			pls_ajuste_fatura	b 
		where	b.nr_sequencia		= a.nr_seq_ajuste_fatura 
		and	b.nr_seq_lote_disc	= nr_seq_lote_disc_w 
		and	a.nr_seq_conta		= nr_seq_conta_w;		
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
