SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_contrato_nr ( nr_contrato_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	E = Estipulante
	D - Data
	S - Descricao do contrato
	P - 
*/
ds_retorno_w		varchar(255);
ds_estipulante_w	varchar(255);
cd_pf_estipulante_w	varchar(10);
cd_cgc_estipulante_w	varchar(14);
dt_contrato_w		timestamp;
ds_contrato_w		varchar(255);
nr_sequencia_w		pls_contrato.nr_sequencia%type;


BEGIN

begin
	select	nr_sequencia,
		cd_pf_estipulante,
		cd_cgc_estipulante,
		dt_contrato
	into STRICT	nr_sequencia_w,
		cd_pf_estipulante_w,
		cd_cgc_estipulante_w,
		dt_contrato_w
	from	pls_contrato
	where	nr_contrato	= nr_contrato_p;
exception
when others then
	cd_pf_estipulante_w	:= null;
	cd_cgc_estipulante_w	:= null;
end;

if (cd_pf_estipulante_w IS NOT NULL AND cd_pf_estipulante_w::text <> '') then
	select	obter_nome_pf(b.cd_pessoa_fisica) nm_pessoa_fisica,
		a.ds_contrato
	into STRICT	ds_estipulante_w,
		ds_contrato_w
	from	pessoa_fisica	b,
		pls_contrato	a
	where	a.cd_pf_estipulante	= b.cd_pessoa_fisica
	and	a.nr_contrato		= nr_contrato_p;
elsif (cd_cgc_estipulante_w IS NOT NULL AND cd_cgc_estipulante_w::text <> '') then
	select	b.ds_razao_social,
		a.ds_contrato
	into STRICT	ds_estipulante_w,
		ds_contrato_w
	from	pessoa_juridica	b,
		pls_contrato	a
	where	a.cd_cgc_estipulante	= b.cd_cgc
	and	a.nr_contrato		= nr_contrato_p;
end if;

if (ie_opcao_p = 'E') then	
	ds_retorno_w	:= ds_estipulante_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= to_char(dt_contrato_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= ds_estipulante_w;
	if (ds_contrato_w IS NOT NULL AND ds_contrato_w::text <> '') then
		ds_retorno_w := substr(ds_retorno_w ||' - '||ds_contrato_w,1,255);
	end if;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := nr_sequencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
