SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_cooperativa ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

						
/*
IE_OPCAO_P
C - Código
RS - Razão social
T - Telefone
NF - Permite NF do refaturamento
CI - Permite A500 do refaturamento
IT- Tipo congenere
DC - Câmara compensação
CC - Código da camara compensação
UF -  UF da cooperativa
*/
ds_retorno_w	varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_opcao_p = 'RS') then
		select	max(substr(b.ds_razao_social,1,255))
		into STRICT	ds_retorno_w
		from	pessoa_juridica b,
			pls_congenere a
		where	a.cd_cgc	= b.cd_cgc
		and	a.nr_sequencia	= nr_sequencia_p;
		
	elsif (ie_opcao_p = 'C') then
		select	a.cd_cooperativa
		into STRICT	ds_retorno_w
		from	pls_congenere a
		where	a.nr_sequencia	= nr_sequencia_p;
		
	elsif (ie_opcao_p = 'T') then
		select	max(b.nr_telefone)
		into STRICT	ds_retorno_w
		from	pessoa_juridica b,
			pls_congenere a
		where	a.cd_cgc	= b.cd_cgc
		and	a.nr_sequencia	= nr_sequencia_p;
		
	elsif (ie_opcao_p = 'NF') then
		select	coalesce(max(a.ie_permite_nf_refat),'S')
		into STRICT	ds_retorno_w
		from	pls_congenere a
		where	a.nr_sequencia	= nr_sequencia_p;
		
	elsif (ie_opcao_p = 'CI') then
		select	coalesce(max(a.ie_permite_a500_refat),'S')
		into STRICT	ds_retorno_w
		from	pls_congenere a
		where	a.nr_sequencia	= nr_sequencia_p;
	
	elsif (ie_opcao_p = 'IT') then
		select	max(a.ie_tipo_congenere)
		into STRICT	ds_retorno_w
		from	pls_congenere a
		where	a.nr_sequencia	= nr_sequencia_p;
	
	elsif (ie_opcao_p = 'DC') then
		select	substr(max(cc.ds_camara),1,255)
		into STRICT	ds_retorno_w
		from	pls_camara_compensacao	cc,
			pls_congenere_camara	gc
		where	cc.nr_sequencia	= gc.nr_seq_camara
		and	cc.ie_situacao	= 'A'
		and	gc.nr_seq_congenere = nr_sequencia_p;
		
	elsif (ie_opcao_p = 'CC') then
		select	substr(max(cc.nr_sequencia),1,255)
		into STRICT	ds_retorno_w
		from	pls_camara_compensacao	cc,
			pls_congenere_camara	gc
		where	cc.nr_sequencia	= gc.nr_seq_camara
		and	cc.ie_situacao	= 'A'
		and	gc.nr_seq_congenere = nr_sequencia_p;
		
	elsif (ie_opcao_p = 'UF') then
		select	substr(max(obter_dados_pf_pj(null, a.cd_cgc, 'UF')),1,255)
		into STRICT	ds_retorno_w
		from	pls_congenere	a
		where	a.nr_sequencia	= nr_sequencia_p;
	end if;	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
