SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_coop_codigo ( cd_cooperativa_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_cooperativa_w		bigint := somente_numero(cd_cooperativa_p);

/* IE_OPCAO_P
NF - Nome fantasia
CD - CNPJ
CC - Conta Contabil
EC - Endereço completo
TF - Telefone e fax
C  - Câmara de compensação*/
BEGIN
if (cd_cooperativa_p IS NOT NULL AND cd_cooperativa_p::text <> '') then
	if (upper(ie_opcao_p) = 'NF') then
		begin
		select	substr(max(b.nm_fantasia),1,80)
		into STRICT	ds_retorno_w
		from	pessoa_juridica	b,
			pls_congenere	a
		where	a.cd_cgc		= b.cd_cgc
		and	a.cd_cooperativa	= cd_cooperativa_p;
		exception
		when others then
			ds_retorno_w	:= null;
		end;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	substr(max(b.nm_fantasia),1,80)
			into STRICT	ds_retorno_w
			from	pessoa_juridica	b,
				pls_congenere	a
			where	a.cd_cgc		= b.cd_cgc
			and	a.cd_cooperativa_number	= cd_cooperativa_w;
		end if;

	elsif (upper(ie_opcao_p) = 'CD') then
		select	substr(max(a.cd_cgc),1,80)
		into STRICT	ds_retorno_w
		from	pls_congenere	a
		where	a.cd_cooperativa_number	= cd_cooperativa_w;

	elsif (upper(ie_opcao_p) = 'CC') then
		select	max(b.cd_conta_contabil)
		into STRICT	ds_retorno_w
		from	pessoa_juridica	b,
			pls_congenere	a
		where	a.cd_cgc		= b.cd_cgc
		and	a.cd_cooperativa_number	= cd_cooperativa_w;

	elsif (upper(ie_opcao_p) = 'EC') then
		begin
		select	substr(coalesce(b.ds_endereco,' '),1,40) 	|| ', ' ||
			substr(coalesce(b.nr_endereco,' '),1,10) 	|| ' - '||
			substr(coalesce(b.ds_complemento,' '),1,40)	|| ' CEP ' ||
			substr(coalesce(b.cd_cep,' '),1,15)		|| ' ' ||
			substr(coalesce(b.ds_municipio,' '),1,40)	|| ' ' ||
			substr(coalesce(b.sg_estado,' '),1,2) ds_endereco_completo
		into STRICT	ds_retorno_w
		from	pessoa_juridica 	b,
			pls_congenere		a
		where	b.cd_cgc		= a.cd_cgc
		and	a.cd_cooperativa_number	= cd_cooperativa_w;
		exception
			when others then
			ds_retorno_w := '';
		end;
	elsif (upper(ie_opcao_p) = 'TF') then
		begin
		select	CASE WHEN coalesce(b.nr_telefone::text, '') = '' THEN ' '  ELSE 'Telefone ('|| substr(coalesce(b.nr_ddd_telefone,'XX'),1,2) || ') ' || substr(coalesce(b.nr_telefone,' '),1,15) END  ||
			CASE WHEN coalesce(b.nr_fax::text, '') = '' THEN ' '  ELSE ' Fax (' || substr(coalesce(b.nr_ddd_fax,'XX'),1,2) || ') ' || substr(coalesce(b.nr_fax,' '),1,15) END
		into STRICT	ds_retorno_w
		from	pessoa_juridica 	b,
			pls_congenere		a
		where	b.cd_cgc		= a.cd_cgc
		and	a.cd_cooperativa_number	= cd_cooperativa_w;
		exception
			when others then
			ds_retorno_w := '';
		end;
	elsif (upper(ie_opcao_p) = 'C') then
		select	max(c.ds_camara)
		into STRICT	ds_retorno_w
		from	pls_camara_compensacao	c,
			pls_congenere_camara 	b,
			pls_congenere		a
		where	a.nr_sequencia		= b.nr_seq_congenere
		and	c.nr_sequencia		= b.nr_seq_camara
		and	a.cd_cooperativa_number	= cd_cooperativa_w
		and	clock_timestamp() between b.dt_inicio_vigencia_ref and b.dt_fim_vigencia_ref;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_coop_codigo ( cd_cooperativa_p text, ie_opcao_p text) FROM PUBLIC;

