SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_declaracao ( cd_pessoa_fisica_p text, nr_seq_proposta_p bigint, nr_seq_contrato_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_RETORNO_P
	P - Peso
	A - Altura
	I - IMC
	S - Status declaração
*/
ds_retorno_w			varchar(255);
nr_seq_declaracao_w		pls_declaracao_segurado.nr_sequencia%type;
qt_peso_w			pls_declaracao_segurado.qt_peso%type;
qt_altura_cm_w			pls_declaracao_segurado.qt_altura_cm%type;
qt_imc_w			pls_declaracao_segurado.qt_imc%type;
ie_status_w			pls_declaracao_segurado.ie_status%type;


BEGIN

if (coalesce(nr_seq_proposta_p,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_declaracao_w
	from	pls_declaracao_segurado
	where	nr_seq_proposta_adesao	= nr_seq_proposta_p;
elsif (coalesce(nr_seq_contrato_p,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_declaracao_w
	from	pls_declaracao_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p;
elsif (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_declaracao_w
	from	pls_declaracao_segurado
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

if (nr_seq_declaracao_w IS NOT NULL AND nr_seq_declaracao_w::text <> '') then
	select	qt_peso,
		qt_altura_cm,
		qt_imc,
		ie_status
	into STRICT	qt_peso_w,
		qt_altura_cm_w,
		qt_imc_w,
		ie_status_w
	from	pls_declaracao_segurado
	where	nr_sequencia	= nr_seq_declaracao_w;

	if (ie_tipo_retorno_p	= 'P') then
		ds_retorno_w		:= qt_peso_w;
	elsif (ie_tipo_retorno_p	= 'A') then
		ds_retorno_w		:= qt_altura_cm_w;
	elsif (ie_tipo_retorno_p	= 'I') then
		ds_retorno_w		:= qt_imc_w;
	elsif (ie_tipo_retorno_p	= 'S') then
		select	substr(obter_valor_dominio(2400,ie_status_w),1,100)
		into STRICT	ds_retorno_w
		;
	end if;
else
	ds_retorno_w	:= null;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
