CREATE OR REPLACE FUNCTION pls_obter_dados_diaria_autor (nr_seq_guia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE



qt_dia_autorizado_w		bigint	:= 0;
qt_dia_prorrogacao_w		bigint	:= 0;
qt_dia_autor_ref_w		bigint	:= 0;
nr_Seq_guia_refer_w		bigint;
ds_retorno_w			varchar(255);
ds_guia_refer_w			varchar(255);

c01 CURSOR FOR
	SELECT	coalesce(qt_dia_autorizado,0),
		nr_sequencia
	from	pls_guia_plano
	where	nr_seq_guia_principal	= nr_seq_guia_p;

BEGIN

if (coalesce(nr_seq_guia_p,0) > 0) then

	qt_dia_prorrogacao_w	:= 0;

	open c01;
	loop
	fetch c01 into
		qt_dia_autor_ref_w,
		nr_Seq_guia_refer_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		qt_dia_prorrogacao_w := qt_dia_prorrogacao_w + qt_dia_autor_ref_w;
		ds_guia_refer_w	     := ds_guia_refer_w || nr_Seq_guia_refer_w || ' ';

	end loop;
	close c01;

	select	coalesce(qt_dia_autorizado,0)
	into STRICT	qt_dia_autorizado_w
	from	pls_guia_plano
	where	nr_sequencia		= nr_seq_guia_p;


	qt_dia_autorizado_w	:= coalesce(qt_dia_autorizado_w,0) + coalesce(qt_dia_prorrogacao_w,0);
end if;

if (ie_opcao_p = 1) then
	ds_retorno_w	:= ds_guia_refer_w;
else
	ds_retorno_w	:= qt_dia_autorizado_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
