SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_discussao ( nr_seq_lote_disc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
					

BEGIN 
if (upper(ie_opcao_p) = 'LF') then 
	select	max(nr_sequencia) 
	into STRICT	ds_retorno_w 
	from	pls_lote_faturamento 
	where	nr_seq_lote_disc	= nr_seq_lote_disc_p;
	 
elsif (upper(ie_opcao_p) = 'LE') then 
	select	max(a.nr_sequencia) 
	into STRICT	ds_retorno_w 
	from	pls_evento_movimento	b, 
		pls_lote_evento		a 
	where	a.nr_sequencia		= b.nr_seq_lote	 
	and	b.nr_seq_lote_disc 	= nr_seq_lote_disc_p;
	 
elsif (upper(ie_opcao_p) = 'LA') then 
	select	max(nr_sequencia) 
	into STRICT	ds_retorno_w 
	from	pls_ajuste_fatura 
	where	nr_seq_lote_disc	= nr_seq_lote_disc_p;
	 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_discussao ( nr_seq_lote_disc_p bigint, ie_opcao_p text) FROM PUBLIC;

