CREATE OR REPLACE FUNCTION pls_obter_dados_email_ocor_fin ( nr_seq_regra_p pls_regra_email_ocor_fin.nr_sequencia%type, ie_opcao_p text, nr_seq_prestador_p pls_prestador.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
cd_cgc_w		pessoa_juridica.cd_cgc%type;


BEGIN
if (ie_opcao_p = 'MS') then
	select	max(ds_mensagem)
	into STRICT	ds_retorno_w
	from	pls_regra_email_ocor_fin
	where	nr_sequencia	= nr_seq_regra_p;

elsif (ie_opcao_p = 'EO') then
	select	max(ds_email_origem)
	into STRICT	ds_retorno_w
	from	pls_regra_email_ocor_fin
	where	nr_sequencia	= nr_seq_regra_p;

elsif (ie_opcao_p = 'OED') then
	select	max(ie_email_destino)
	into STRICT	ds_retorno_w
	from	pls_regra_email_ocor_fin
	where	nr_sequencia	= nr_seq_regra_p;

elsif (ie_opcao_p = 'ED') then
	select	max(ie_email_destino)
	into STRICT	ds_retorno_w
	from	pls_regra_email_ocor_fin
	where	nr_sequencia	= nr_seq_regra_p;

	if (ds_retorno_w = 'PR') then -- Prestador
		ds_retorno_w := substr(pls_obter_email_prestador(nr_seq_prestador_p),1,255);

	elsif (ds_retorno_w = 'CO') then -- Cooperado
		select	max(cd_pessoa_fisica),
			max(cd_cgc_w)
		into STRICT	cd_pessoa_fisica_w,
			cd_cgc_w
		from	pls_prestador
		where	nr_sequencia	= nr_seq_prestador_p;

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			select	substr(pls_obter_dados_cooperado(max(nr_sequencia),'EM'),1,255)
			into STRICT	ds_retorno_w
			from	pls_cooperado
			where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

		elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
			select	substr(pls_obter_dados_cooperado(max(nr_sequencia),'EM'),1,255)
			into STRICT	ds_retorno_w
			from	pls_cooperado
			where	cd_cgc	= cd_cgc_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
